}

impl RepositoryExt for Repository {
  fn get_last_n_commits(&self, max_commits: usize, max_tokens: usize) -> Result<Vec<Payload>, git2::Error> {
    let mut revwalk = self.revwalk()?;
    revwalk.push_head()?;
    Ok(
      revwalk
        .take(max_commits)
